INSERT INTO `haber` (`h_id`, `h_baslik`, `h_icerik`, `h_tarih`) VALUES
(1, 'LHÜ ve TEKNOPARK ANKARA ARASINDA İŞBİRLİĞİ PROTOKLÜ İMZALANDI', 'Değerli Lokman Hekim Ailesi,\r\n\r\nLokman Hekim Üniversitesi, Teknopark Ankara ile 10 Nisan 2019 tarihinde iş birliği, güç birliği oluşturmak ve ortak projeler gerçekleştirmek üzere protokol imzalamıştır.\r\n\r\nLokman Hekim Üniversitesi ve Teknopark Ankara, eğitim, danışmanlık, ortak Ar-Ge faaliyetlerinde bulunma, teknoloji geliştirme/teknoloji kazanım projeleri oluşturma/yürütme, ortak seminer ve konferanslar düzenlemek, karşılıklı olarak birbirinin uzmanlıklarından, altyapı, tesis gibi imkanlarından yararlanabilmek, ortak akademik çalışma yürütmek, etkinlik ve erişebilirliklerini artırıcı (mal ve hizmetler) ürünlerin tasarlanması, üretilmesi ve satışı konularını kapsayacak şekilde iş birliği protokolü imzalamışlardır.\r\n\r\nÜniversitemize ve Teknopark Ankara’ya hayırlı olmasını diliyoruz.\r\n\r\nSaygılarımızla,\r\n\r\nLokman Hekim Üniversitesi ', '2019-01-01'),
(5, '”TÜRK SPORUNDA ANTRENÖRLÜĞÜN DÜNÜ, BUGÜNÜ VE YARINI” PANELİ', '<p>&nbsp;<span style=\"color: rgb(77, 77, 79); font-family: Gotham-Book; font-size: 16.896px; text-align: justify;\">Lokman Hekim &Uuml;niversitesi Spor Bilimleri Fak&uuml;ltesi &ouml;nderliğinde d&uuml;zenlenen &ldquo;T&uuml;rk Sporunda Antren&ouml;rl&uuml;ğ&uuml;n D&uuml;n&uuml;, Bug&uuml;n&uuml; ve Yarını&rdquo; isimli panelde konuşan Teknik Direkt&ouml;r Rıza &Ccedil;alımbay, T&uuml;rk sporunda başarının sırrının disiplin olduğunu anlattı.</span></p>\r\n<div dir=\"auto\" style=\"outline: none; margin: 0px; padding: 0px; max-width: 100%; color: rgb(77, 77, 79); font-family: Gotham-Book; font-size: 16.896px; text-align: justify;\">\r\n<p style=\"outline: none; margin: 1vw 0px; padding: 0px;\">&Uuml;niversitemizde Spor Bilimleri Fak&uuml;ltesi Dekanı Sayın Prof. Dr. Feyyaz Onur ve Vakıf Y&ouml;netim Kurulu &Uuml;yemiz Sayın Osman Tekin moderat&ouml;rl&uuml;ğ&uuml;nde ger&ccedil;ekleştirilen panelde, Halter Federasyonu Başkanı Sayın Tamer Taşpınar, Teknik Direkt&ouml;r Sayın Razı &Ccedil;alımbay, ISF Atletizm Komite Başkanı ve FİSU Kontrol Komite &Uuml;yesi Sayın Nihat Doker ve Sayın Do&ccedil;.Dr. Haluk Ko&ccedil; katıldı. Panele katılan konuşmacılar, antren&ouml;rl&uuml;k mesleğine ilişkin deneyimlerini &uuml;niversite &ouml;ğrencileriyle paylaştı.</p>\r\n<div dir=\"auto\" style=\"outline: none; margin: 0px; padding: 0px; max-width: 100%;\">&Ccedil;alımbay, burada yaptığı konuşmada eğitimin &ouml;nemine dikkati &ccedil;ekti. Futbol başta olmak &uuml;zere T&uuml;rk sporunun genelinde alt yapı eksikliğinin en b&uuml;y&uuml;k problem olduğunu vurgulayan &Ccedil;alımbay, &ldquo;Yeterli ve kaliteli alt yapı eğitimini almayan sporcular tam profesyonel olamıyor. Avrupalı sporcular yaptıkları şeyi iş olarak g&ouml;r&uuml;yor ve işlerinin gerektirdiğini eksiksiz yerine getiriyor. Bizim de sporu bir iş olarak g&ouml;r&uuml;p, ona g&ouml;re &ccedil;alışmamız lazım. Disiplin, sporun olmazsa olmazı.&rdquo; dedi.</div>\r\n<div dir=\"auto\" style=\"outline: none; margin: 0px; padding: 0px; max-width: 100%;\">T&uuml;rk antren&ouml;rlerinin Avrupalı meslektaşlarından hi&ccedil;bir eksiğinin bulunmadığını vurgulayan &Ccedil;alımbay, &ldquo;Spor camiasında menajerler &ccedil;ok etkili. T&uuml;rk antren&ouml;rlerin yurt dışında g&ouml;rev almamasında da menajerlerin rol&uuml; var. T&uuml;rk takımları, milli takımımız ne kadar başarılı olursa, antren&ouml;rlerimizin de &ouml;n&uuml; o kadar a&ccedil;ılır. Hepimiz yurt dışında &ccedil;alışabilecek yeteneğe sahibiz. Yurt dışında &ccedil;alışmak buradan daha kolay. &Ccedil;&uuml;nk&uuml; orada herkes işinin gerektirdiği sorumluluğa sahip.&rdquo; diye konuştu.</div>\r\n<p style=\"outline: none; margin: 1vw 0px; padding: 0px;\">&nbsp;</p>\r\n<div class=\"rl-gallery-container\" id=\"rl-gallery-container-2\" data-gallery_id=\"0\" style=\"outline: none; margin: 0px; padding: 0px; max-width: 100%;\">\r\n<div class=\"rl-gallery rl-basicmasonry-gallery \" id=\"rl-gallery-2\" data-gallery_no=\"2\" style=\"outline: none; margin: 0px auto; padding: 0px; position: relative; z-index: 2; max-width: 100%; flex-wrap: wrap !important; justify-content: center !important;\">\r\n<div class=\"rl-gutter-sizer\" style=\"outline: none; margin: 0px; padding: 0px; max-width: 100%;\">&nbsp;</div>\r\n<div class=\"rl-grid-sizer\" style=\"outline: none; margin-top: 0px; margin-right: 0px; margin-bottom: 0px !important; margin-left: 0px; padding: 0px; max-width: 100%;\">&nbsp;</div>\r\n<div class=\"rl-gallery-item rl-gallery-item-width-4\" style=\"outline: none; margin: 0px; padding: 0px; max-width: 100%;\">\r\n<div class=\"rl-gallery-item-content\" style=\"outline: none; margin: 3px; padding: 0px; max-width: 100%;\"><a href=\"https://www.lokmanhekim.edu.tr/wp-content/uploads/2019/05/IMG_4928-1024x683.jpg\" title=\"\" data-rl_title=\"\" class=\"rl-gallery-link\" data-rl_caption=\"\" data-rel=\"lightbox-gallery-2\" style=\"outline: none; margin: 0px; padding: 0px